// Parse the master agent's response and prepare it for child agents
const masterIdea = JSON.parse($input.first().json.message.content);

// Store the master idea for all child agents to access
return [
  {
    json: {
      masterIdea: masterIdea,
      timestamp: new Date().toISOString(),
      childAgentTopics: [
        "Technical Implementation",
        "Marketing Strategy", 
        "Financial Planning",
        "User Experience Design",
        "Scaling & Operations"
      ]
    }
  }
];

// Aggregate all agent outputs into a comprehensive business plan
const items = $input.all();
const masterIdea = items[0].json;
const technicalPlan = JSON.parse(items[1].json.message.content);
const marketingStrategy = JSON.parse(items[2].json.message.content);
const financialPlan = JSON.parse(items[3].json.message.content);
const uxDesign = JSON.parse(items[4].json.message.content);
const scalingPlan = JSON.parse(items[5].json.message.content);

return [{
  json: {
    generatedAt: new Date().toISOString(),
    comprehensiveBusinessPlan: {
      masterConcept: masterIdea.masterIdea,
      detailedPlans: {
        technical: technicalPlan,
        marketing: marketingStrategy,
        financial: financialPlan,
        userExperience: uxDesign,
        scaling: scalingPlan
      },
      executiveSummary: {
        title: masterIdea.masterIdea.businessConcept,
        description: "A comprehensive business plan generated by AI agents",
        keyHighlights: [
          "Technical feasibility validated",
          "Marketing strategy defined",
          "Financial projections calculated",
          "UX optimized for conversions",
          "Scaling roadmap established"
        ]
      }
    }
  }
}];

